mapper.select:
SELECT DISTINCT tre.id, tre.issue_name, tre.status, tre.created_at, tre.updated_at,tre.project_budget,treo.visit_count,treo.apply_count,treo.recommend_count,tre.business_id AS business_id,tre.business_parent_id as business_parent_id,tre.verified AS verified FROM t_recruit_employer tre LEFT JOIN t_recruit_employer_operation treo ON tre.id = treo.employer_id -- LEFT JOIN t_recruit_employer_master_mapping tremm -- ON tre.id = tremm.employer_id WHERE 1=1
and tre.status=#{status}
and tre.business_parent_id = #{businessParentId}
and tre.master_id = #{masterId}
ORDER by tre.created_at DESC

SELECT count(1)
FROM t_recruit_employer
WHERE status=#{status}

    business_parent_id = #{businessParentId}

mapper.insert:
insert into t_recruit_employer (issue_name, company_name,
  trend_complete, project_desc,project_budget,
  status,business_id,business_parent_id,project_cycle,master_id
  )
values (#{projectName,jdbcType=VARCHAR}, #{companyName,jdbcType=VARCHAR},
  #{trendComplete,jdbcType=VARCHAR}, #{projectDesc,jdbcType=VARCHAR},
  #{projectBudget,jdbcType=VARCHAR},#{status,jdbcType=VARCHAR},#{businessId,jdbcType=VARCHAR},#{businessParentId,jdbcType=VARCHAR},#{projectCycle,jdbcType=VARCHAR},#{masterId}
  )

mapper.update:
update t_recruit_employer


        status = #{status,jdbcType=VARCHAR},

    reason = #{reason}

where id = #{id,jdbcType=BIGINT}

UPDATE t_recruit_employer
SET verified = #{verified}
where id = #{empId}

UPDATE t_recruit_employer
SET status = #{status}
where id = #{empId}
