mapper.select:
SELECT trtm.id, trtm.self_introduction,trtm.job_title,trtm.nick_name, trtm.daily_wage,trtm.reason, trtm.city_id,trc.city_name,trtm.sex, trtm.status,trtm.headImgUrl,trtm.verified,trtm.recruit_type, trtm.self_introduction,trtm.work_experience, trtm.phone,tri.id AS industry_id,tri.industry_name FROM t_recruit_tech_master trtm LEFT JOIN t_recruit_city trc ON trtm.city_id = trc.id LEFT JOIN t_recruit_industry tri ON tri.id = trtm.industry_id
LEFT JOIN t_recruit_techmaster_skill_mapping mapping ON trtm.id = mapping.tech_master_id LEFT JOIN t_recruit_base_skill trbs ON mapping.skill_id = trbs.id
WHERE 1=1
and trtm.status = #{status}
and trc.id = #{cityId}
and trtm.industry_id = #{industryId}
and trbs.skill_name LIKE CONCAT(CONCAT('%', #{queryParam}), '%')
and trtm.id = #{id}
ORDER BY trtm.updated_at DESC

mapper.update:
UPDATE t_recruit_tech_master
SET verified = #{verified}
WHERE id = #{masterId}

update t_recruit_tech_master
SET status = #{status,jdbcType=VARCHAR},
    reason = #{reason,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
